
import {Entity, PrimaryColumn, Column} from "typeorm";

@Entity("users")
export default class User {
  @PrimaryColumn("int")
  /* site-wide unique identifier generated by itch.io */
  id: number;

  @Column("text", {nullable: true})
  /** the user's username */
  username: string;

  @Column("text", {nullable: true})
  /** the user's display name */
  displayName: string;

  @Column("boolean", {nullable: true})
  /** true if the user is a developer */
  developer: boolean;

  @Column("boolean", {nullable: true})
  /** true if the user is a press user */
  pressUser: boolean;

  @Column("text", {nullable: true})
  /** the canonical address of the game's page */
  url: string;

  @Column("text", {nullable: true})
  /** user's avatar, may be a gif */
  coverUrl: string;

  @Column("text", {nullable: true})
  /** user's avatar static version, only if coverUrl is a gif */
  stillCoverUrl: string;
}
